path = '/201702/N000001/NTS3032/003'name = 'Theme 6.docx'locate = 'AssPdsProf_U17016_20171228_075610_370_0_1.docx'import urldef download(session, path, name, locate):    downloadform = {        'p_filepath': path,        'p_realfile': name,        'p_savefile': locate    }    doc = session.post(url.base_url + url.source_url, downloadform, stream=True)    with open('./download'+path+'/'+name, 'wb') as f:        for chunk in doc.iter_content(chunk_size=64):            f.write(chunk)    f.close()